import React from "react";
import { View, Text, StyleSheet, ScrollView, Platform, Linking, Pressable } from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";
import { IconSymbol } from "@/components/IconSymbol";
import { GlassView } from "expo-glass-effect";
import { useTheme } from "@react-navigation/native";

export default function ProfileScreen() {
  const theme = useTheme();

  const handleContact = (type: 'phone' | 'whatsapp' | 'instagram') => {
    let url = '';
    if (type === 'phone') {
      url = `tel:${'+96560000000'}`;
    } else if (type === 'whatsapp') {
      url = `https://wa.me/${'+96560000000'}`;
    } else if (type === 'instagram') {
      url = 'https://instagram.com/your_instagram_username'; // Replace with actual Instagram username
    }

    Linking.openURL(url).catch(err => console.error("An error occurred", err));
  };

  return (
    <SafeAreaView style={[styles.safeArea, { backgroundColor: theme.colors.background }]} edges={['top']}>
      <ScrollView
        style={styles.container}
        contentContainerStyle={[
          styles.contentContainer,
          Platform.OS !== 'ios' && styles.contentContainerWithTabBar
        ]}
      >
        <GlassView style={[
          styles.profileHeader,
          Platform.OS !== 'ios' && { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)' }
        ]} glassEffectStyle="regular">
          <IconSymbol name="leaf" size={80} color={theme.colors.primary} />
          <Text style={[styles.name, { color: theme.colors.text }]}>Al-Thunayan Honey Farms</Text>
          <Text style={[styles.email, { color: theme.dark ? '#98989D' : '#666' }]}>info@althunayanhoney.com</Text>
          <Text style={[styles.description, { color: theme.dark ? '#98989D' : '#666', textAlign: 'center' }]}>
            Al-Thunayan Honey Farms is a Kuwait-based honey producer offering premium natural honey products, including royal blends, white royal honey, and kids’ honey.
          </Text>
        </GlassView>

        <GlassView style={[
          styles.section,
          Platform.OS !== 'ios' && { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)' }
        ]} glassEffectStyle="regular">
          <Pressable style={styles.infoRow} onPress={() => handleContact('phone')}>
            <IconSymbol name="phone.fill" size={20} color={theme.dark ? '#98989D' : '#666'} />
            <Text style={[styles.infoText, { color: theme.colors.text }]}>+965 6000 0000</Text>
          </Pressable>
          <View style={styles.infoRow}>
            <IconSymbol name="location.fill" size={20} color={theme.dark ? '#98989D' : '#666'} />
            <Text style={[styles.infoText, { color: theme.colors.text }]}>Al-Wafra, Kuwait</Text>
          </View>
          <Pressable style={styles.infoRow} onPress={() => handleContact('whatsapp')}>
            <IconSymbol name="logo.whatsapp" size={20} color={theme.dark ? '#98989D' : '#666'} />
            <Text style={[styles.infoText, { color: theme.colors.text }]}>WhatsApp</Text>
          </Pressable>
           <Pressable style={styles.infoRow} onPress={() => handleContact('instagram')}>
            <IconSymbol name="logo.instagram" size={20} color={theme.dark ? '#98989D' : '#666'} />
            <Text style={[styles.infoText, { color: theme.colors.text }]}>Instagram</Text>
          </Pressable>
        </GlassView>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  safeArea: {
    flex: 1,
    // backgroundColor handled dynamically
  },
  container: {
    flex: 1,
  },
  contentContainer: {
    padding: 20,
  },
  contentContainerWithTabBar: {
    paddingBottom: 100, // Extra padding for floating tab bar
  },
  profileHeader: {
    alignItems: 'center',
    borderRadius: 12,
    padding: 32,
    marginBottom: 16,
    gap: 12,
  },
  name: {
    fontSize: 24,
    fontWeight: 'bold',
    // color handled dynamically
  },
  email: {
    fontSize: 16,
    // color handled dynamically
  },
   description: {
    fontSize: 14,
  },
  section: {
    borderRadius: 12,
    padding: 20,
    gap: 12,
  },
  infoRow: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 12,
  },
  infoText: {
    fontSize: 16,
    // color handled dynamically
  },
});
